package org.course.management.controller;import lombok.RequiredArgsConstructor;import org.course.management.entity.Course;import org.course.management.entity.Rate;import org.course.management.entity.Topic;import org.course.management.entity.User;import org.course.management.service.CourseService;import org.course.management.service.RateService;import org.course.management.service.TopicService;import org.course.management.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import java.util.List;@Controller@RequiredArgsConstructor@RequestMapping("/admin")public class AdminController {    private final UserService userService;    private final RateService rateService;    private final TopicService topicService;    private final CourseService courseService;    @GetMapping({"", "/trang-chu"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentPage", "trang-chu");        return "admin/home";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        List<Course> list = courseService.getAllCourse();        model.addAttribute("courses", list);        model.addAttribute("currentPage", "khoa-hoc");        return "admin/course";    }    @GetMapping("/chuyen-de")    public String ViewChuyenDe(Model model) {        List<Topic> list = topicService.getAllTopic();        model.addAttribute("topics", list);        model.addAttribute("currentPage", "chuyen-de");        return "admin/topic";    }    @GetMapping("/nguoi-dung")    public String ViewNguoiDung(Model model) {        List<User> list = userService.findAllUser();        model.addAttribute("users", list);        model.addAttribute("currentPage", "nguoi-dung");        return "admin/user";    }    @GetMapping("/danh-gia")    public String ViewDanhGia(Model model) {        List<Rate> list = rateService.getAllRate();        model.addAttribute("rates", list);        model.addAttribute("currentPage", "danh-gia");        return "admin/rate";    }}