package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.fasterxml.jackson.annotation.JsonManagedReference;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_enroll")public class Enroll {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "enroll_id")    private int enrollId;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "course_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Course course;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "user_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private User user;    @OneToMany(mappedBy = "enroll")    @JsonIgnore    private List<Rate> rates;    @OneToMany(mappedBy = "enroll")    @JsonIgnore    private List<CourseDetail> courseDetails;}